import { Action, toAction } from 'node-rx';

export class GreetingActions {
    static readonly CLIENT_LOGIN = 'CLIENT_LOGIN';
    static login( delay: number ): Action { return toAction( ClientActions.CLIENT_LOGIN, token ) }

    static readonly CLIENT_LOGIN_SUCCEEDED = 'CLIENT_LOGIN_SUCCEEDED';
    static loginSucceeded( token: string ): Action { return toAction( ClientActions.CLIENT_LOGIN_SUCCEEDED, token ) }

    static readonly CLIENT_LOGIN_FAILED = 'CLIENT_LOGIN_FAILED';
    static loginFailed(): Action { return toAction( ClientActions.CLIENT_LOGIN_FAILED ) }

    static readonly CLIENT_LOGOUT = 'CLIENT_LOGOUT';
    static logout(): Action { return toAction( ClientActions.CLIENT_LOGOUT ) }
    
    static readonly CLIENT_LOGOUT_SUCCEEDED = 'CLIENT_LOGOUT_SUCCEEDED';
    static logoutSucceeded(): Action { return toAction( ClientActions.CLIENT_LOGOUT_SUCCEEDED ) }
    
    static readonly CLIENT_LOGOUT_FAILED = 'CLIENT_LOGOUT_FAILED';
    static logoutFailed(): Action { return toAction( ClientActions.CLIENT_LOGOUT_FAILED ) }
}
